import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import "./App.css";
import Home from "./pages/home";
import MainWeather from "./pages/mainweather";
import database from "../api/openWeatherMap";

function App() {
  const [cityName, setCityName] = useState("");
  const [dataLoaded, setDataLoaded] = useState(false);
  const [cityData, setCityData] = useState(null);
  const [weatherData, setWeatherData] = useState(null);

  async function fetchWeather(choosenCityName) {
    const weather = await database.getWeatherByCityName(choosenCityName);
    if (weather == false) {
      setCityNotFound(true);
    } else {
      setCityData(weather[0]);
      setWeatherData(weather[1]);
      setCityNotFound(false);
    }
    setDataLoaded(true);
  }

  return (
    <Router>
      <div>
        <Switch>
          <Route path='/mainweather'>
            <MainWeather />
          </Route>
          <Route
            exact
            path='/'
            render={props => <Home {...props} setCityName={setCityName} />}
          ></Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
