import React, { useState, useEffect } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core";
import SunIcon from "../assets/images/sun.png";
import CloudIcon from "../assets/images/cloud.png";
import RainIcon from "../assets/images/rain.png";
import HailIcon from "../assets/images/hail.png";
import StormIcon from "../assets/images/storm.png";
import HeavyRainIcon from "../assets/images/heavy-rain.png";
import CloudsAndSunIcon from "../assets/images/clouds-and-sun.png";
import FogIcon from "../assets/images/fog.png";
import SnowIcon from "../assets/images/snow.png";

const useStyles = makeStyles({
  smallContainer: {
    backgroundColor: "#273DFF",
    color: "#EFEFEF",
    padding: 10,
    margin: 20,
    marginLeft: 100,
    marginRight: 100,
    borderRadius: 10,
  },
  averageIcon: {
    width: 80,
    height: 80,
  },
  minMaxTemp: {
    textAlign: "right",
  },
});

function DayWindow() {
  const classes = useStyles();
  const [averageIcon, setAverageIcon] = useState(null);
  const [minTemp, setMinTemp] = useState("");
  const [maxTemp, setMaxTemp] = useState("");

  async function fetch() {
    setAverageIcon(SunIcon);
    setMinTemp(3 + "°");
    setMaxTemp(12 + "°");
  }

  useEffect(() => {
    fetch();
  }, []);

  return (
    <div className={classes.smallContainer}>
      <Grid container spacing={6}>
        <Grid item xs={2}>
          <img src={averageIcon} className={classes.averageIcon} />
        </Grid>
        <Grid item xs={2}>
          {maxTemp} <br /> {minTemp}
        </Grid>
        <Grid item xs={2}>
          Normalnie w mordę jeża
        </Grid>
        <Grid item xs={2}>
          Panie Ferdku daj pan pieroga
        </Grid>
        <Grid item xs={2}>
          Gorzała za mną chodzi od rana
        </Grid>
        <Grid item xs={2}>
          Panie nie obrażaj pan dobre?
        </Grid>
      </Grid>
    </div>
  );
}

export default DayWindow;
