import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import Select from "@material-ui/core/Select";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import InputBase from "@material-ui/core/InputBase";
import MenuItem from "@material-ui/core/MenuItem";
import Button from "@material-ui/core/Button";
import SearchIcon from "@material-ui/icons/Search";

const useStyles = makeStyles({
  cityWindow: {
    margin: "auto",
    padding: 10,
    paddingLeft: 30,
    height: 30,
    width: 200,
    borderRadius: 8,
    backgroundColor: "white",
    display: "flex",
    border: "2px solid #141414",
  },
  formControl: {
    width: 150,
  },
});

function StatisticalSearch() {
  const classes = useStyles();
  const [month, setMonth] = useState(0);
  const [day, setDay] = useState(0);
  const [dayList, setDayList] = useState([]);
  const [dayDisabled, setDayDisabled] = useState(true);
  const [city, setCity] = useState("");
  const handleChange = e => setCity(e.target.value);

  const handleMonthChange = event => {
    setMonth(event.target.value);
    setDay(0);
    let dayListTemp = [];
    let numberOfDays = 0;
    if ([1, 3, 5, 7, 8, 10, 12].includes(event.target.value)) {
      numberOfDays = 31;
    } else if (event.target.value == 2) {
      numberOfDays = 29;
    } else {
      numberOfDays = 30;
    }
    for (let i = 0; i < numberOfDays; i++) {
      dayListTemp.push(i + 1);
    }
    setDayList(dayListTemp);
    console.log(dayListTemp);
  };

  const handleDayChange = event => {
    setDay(event.target.value);
  };

  function handleSearchButtonClick() {
    setCity("");
    console.log(city);
    //props.handleSearch(value);
  }

  useEffect(() => {
    if (dayList.length > 0) {
      setDayDisabled(false);
    }
  }, [dayList]);

  console.log(day);
  return (
    <div>
      <Grid container spacing={4}>
        <Grid item xs>
          <div className={classes.cityWindow}>
            <InputBase
              placeholder='Search city'
              value={city}
              onChange={handleChange}
            />
          </div>
        </Grid>
        <Grid item xs>
          <FormControl variant='outlined' className={classes.formControl}>
            <InputLabel id='month-label'>Month</InputLabel>
            <Select
              labelId='month-label'
              value={month}
              onChange={handleMonthChange}
              label='Month'
            >
              <MenuItem value={1}>January</MenuItem>
              <MenuItem value={2}>February</MenuItem>
              <MenuItem value={3}>March</MenuItem>
              <MenuItem value={4}>April</MenuItem>
              <MenuItem value={5}>May</MenuItem>
              <MenuItem value={6}>June</MenuItem>
              <MenuItem value={7}>July</MenuItem>
              <MenuItem value={8}>August</MenuItem>
              <MenuItem value={9}>September</MenuItem>
              <MenuItem value={10}>October</MenuItem>
              <MenuItem value={11}>November</MenuItem>
              <MenuItem value={12}>December</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs>
          <FormControl
            className={classes.formControl}
            variant='outlined'
            disabled={dayDisabled}
          >
            <InputLabel id='day-label'>Day</InputLabel>
            <Select
              labelId='day-label'
              value={day}
              onChange={handleDayChange}
              label='Day'
            >
              {dayList.length > 0 &&
                dayList.map(day => <MenuItem value={day}>{day}</MenuItem>)}
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs>
          <Button onClick={handleSearchButtonClick}>
            <SearchIcon />
          </Button>
        </Grid>
      </Grid>
    </div>
  );
}

export default StatisticalSearch;
