import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import TemperatureBox from "../components/temperature-box";
import SmallBox from "../components/small-box";

const useStyles = makeStyles({
  cityText: {
    fontSize: 20,
    textAlign: "center",
  },
  smallBox: {
    marginBottom: 50,
  },
  containerLeft: {
    marginRight: 50,
  },
  containerRight: {
    marginLeft: 50,
  },
});

const StatisticalDisplay = ({ cityData, statisticalData }) => {
  const classes = useStyles();

  return (
    <div>
      <div className={classes.cityText}>
        <p>Warsaw, PL</p>
        <p>Statistical data for 10th of May</p>
      </div>
      <Grid container direction='row' spacing={10}>
        <Grid
          item
          container
          direction='column'
          xs
          className={classes.containerLeft}
        >
          <Grid item className={classes.smallBox}>
            <SmallBox label='label' value='100' units='W' />
          </Grid>
          <Grid item>
            <SmallBox label='label' value='100' units='W' />
          </Grid>
        </Grid>
        <Grid item xs>
          <TemperatureBox average='10' max='13' min='8' />
        </Grid>
        <Grid
          item
          container
          direction='column'
          xs
          className={classes.containerRight}
        >
          <Grid item className={classes.smallBox}>
            <SmallBox label='label' value='100' units='W' />
          </Grid>
          <Grid item>
            <SmallBox label='label' value='100' units='W' />
          </Grid>
        </Grid>
      </Grid>
    </div>
  );
};

export default StatisticalDisplay;
