import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import Select from "@material-ui/core/Select";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";

const useStyles = makeStyles({
  cityWindow: {
    margin: "auto",
    padding: 10,
    paddingLeft: 30,
    height: 30,
    width: 300,
    borderRadius: 8,
    backgroundColor: "white",
    display: "flex",
    border: "2px solid #141414",
  },
  formControl: {
    width: 150,
  },
});

function StatisticalSearch() {
  const classes = useStyles();
  const [month, setMonth] = useState(0);
  const [day, setDay] = useState(0);
  const [dayList, setDayList] = useState([]);
  const [dayDisabled, setDayDisabled] = useState(true);

  const handleMonthChange = event => {
    setMonth(event.target.value);
    setDayList(0);
    let dayListTemp = [];
    let numberOfDays = 0;
    if ([1, 3, 5, 7, 8, 10, 12].includes(event.target.value)) {
      numberOfDays = 31;
    } else if (event.target.value == 2) {
      numberOfDays = 29;
    } else {
      numberOfDays = 30;
    }
    for (let i = 0; i < numberOfDays; i++) {
      dayListTemp.push(i + 1);
    }
    setDayList(dayListTemp);
    console.log(dayListTemp);
  };

  const handleDayChange = event => {
    setDay(event.target.value);
  };

  useEffect(() => {
    if (dayList.length > 0) {
      setDayDisabled(false);
    }
  }, [dayList]);

  console.log(day);
  return (
    <div>
      <Grid container>
        <Grid item>
          <div className={classes.cityWindow}>Siema</div>
        </Grid>
        <Grid item>
          <FormControl className={classes.formControl}>
            <InputLabel id='month-label'>Month</InputLabel>
            <Select
              labelId='month-label'
              value={month}
              onChange={handleMonthChange}
            >
              <MenuItem value={1}>January</MenuItem>
              <MenuItem value={2}>February</MenuItem>
              <MenuItem value={3}>March</MenuItem>
              <MenuItem value={4}>April</MenuItem>
              <MenuItem value={5}>May</MenuItem>
              <MenuItem value={6}>June</MenuItem>
              <MenuItem value={7}>July</MenuItem>
              <MenuItem value={8}>August</MenuItem>
              <MenuItem value={9}>September</MenuItem>
              <MenuItem value={10}>October</MenuItem>
              <MenuItem value={11}>November</MenuItem>
              <MenuItem value={12}>December</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item>
          <FormControl className={classes.formControl} disabled={dayDisabled}>
            <InputLabel id='day-label'>Day</InputLabel>
            <Select labelId='day-label' value={day} onChange={handleDayChange}>
              {dayList.length > 0 &&
                dayList.map(day => <MenuItem value={day}>{day}</MenuItem>)}
            </Select>
          </FormControl>
        </Grid>
      </Grid>
    </div>
  );
}

export default StatisticalSearch;
