import React from "react";
import { makeStyles } from "@material-ui/core";
import { Link } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import PlayArrowIcon from "@material-ui/icons/PlayArrow";
import logo from "../assets/images/logo.png";

const useStyles = makeStyles({
  mainStyle: {
    fontFamily: "Open Sans",
  },
  returnArrow: {
    transform: "rotate(180deg)",
    margin: 30,
    fontSize: 60,
    color: "#C4C4C4",
  },
  topGrid: {
    marginTop: -60,
    borderBottom: "solid",
    borderBottomWidth: 1,
  },
  logo: {
    width: "40%",
    float: "right",
    marginRight: 50,
  },
  mainText: {
    marginLeft: 80,
    marginRight: 100,
    fontSize: 22,
  },
});

function About() {
  const classes = useStyles();

  return (
    <div className={classes.mainStyle}>
      <Link to='/' style={{ textDecoration: "none" }}>
        <PlayArrowIcon className={classes.returnArrow} />
      </Link>
      <Grid container className={classes.topGrid}>
        <Grid item xs={5}>
          <img src={logo} className={classes.logo} />
        </Grid>
        <Grid item xs={7}>
          <p className={classes.mainText}>
            MyWeather is a project of Krzysztof Moszczy≈Ñski, Information
            Technology student at Technical University of Lodz. The purpose of
            this project is to create an interesting web application displaying
            weather data for specified city. The application uses OpenWeatherMap
            API as a source of data.
          </p>
        </Grid>
      </Grid>
    </div>
  );
}

export default About;
