import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core";
import grey from "@material-ui/core/colors/grey";

const useStyles = makeStyles(theme => ({
  clockStyle: {
    background: grey[100],
    width: 200,
    height: 100,
    paddingRight: 10,
    paddingBottom: 5,
    textAlign: "right",
  },
  upperHour: {
    fontSize: 30,
    fontWeight: "bold",
  },
  lowerDate: {
    fontSize: 20,
  },
}));

function Clock(props) {
  //const time = new Date();
  const classes = useStyles();
  const [time, setTime] = useState(null);
  const [seconds, setSeconds] = useState(0);

  function fetchTime() {
    let newTime = new Date();
    if (newTime.getSeconds() < 10) {
      setSeconds("0" + newTime.getSeconds());
    } else {
      setSeconds(newTime.getSeconds());
    }
    setTime(newTime);
    console.log(seconds);
  }

  useEffect(() => {
    fetchTime();
    const interval = setInterval(() => {
      console.log("jebać pis");
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  if (time) {
    return (
      <div className={classes.clockStyle}>
        <p className={classes.upperHour}>
          {time.getHours()}:{time.getMinutes()}:{seconds}
        </p>
        <p className={classes.lowerDate}>
          {time.getUTCMonth()} {time.getDay()} {time.getFullYear()}
        </p>
      </div>
    );
  } else {
    return <></>;
  }
}

export default Clock;
