import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import Select from "@material-ui/core/Select";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";

const useStyles = makeStyles({
  cityWindow: {
    margin: "auto",
    padding: 10,
    paddingLeft: 30,
    height: 30,
    width: 300,
    borderRadius: 8,
    backgroundColor: "white",
    display: "flex",
    border: "2px solid #141414",
  },
  formControl: {
    width: 150,
  },
});

function StatisticalSearch() {
  const classes = useStyles();
  const [month, setMonth] = useState(0);
  const [day, setDay] = useState(0);

  const handleMonthChange = event => {
    setMonth(event.target.value);
  };

  const handleDayChange = event => {
    setDay(event.target.value);
  };

  return (
    <div>
      <Grid container>
        <Grid item>
          <div className={classes.cityWindow}>Siema</div>
        </Grid>
        <Grid item>
          <FormControl className={classes.formControl}>
            <InputLabel id='month-label'>Month</InputLabel>
            <Select
              labelId='month-label'
              value={month}
              onChange={handleMonthChange}
            >
              <MenuItem value={1}>January</MenuItem>
              <MenuItem value={2}>February</MenuItem>
              <MenuItem value={3}>January</MenuItem>
              <MenuItem value={4}>January</MenuItem>
              <MenuItem value={5}>January</MenuItem>
              <MenuItem value={6}>January</MenuItem>
              <MenuItem value={7}>January</MenuItem>
              <MenuItem value={8}>January</MenuItem>
              <MenuItem value={9}>January</MenuItem>
              <MenuItem value={10}>January</MenuItem>
              <MenuItem value={11}>January</MenuItem>
              <MenuItem value={12}>January</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item></Grid>
      </Grid>
    </div>
  );
}

export default StatisticalSearch;
